mapper.select:
select * from shopping_store

  1=1
   and  store_recommend=#{store_recommend}  
   order by ${orderBy}  ${sort}
   limit #{start},#{pageSize}

select count(*) from shopping_store s
left  join shopping_area a    on s.area_id =a.id
left join  shopping_store_point p on p.store_id= s.id

  1=1
   and  s.store_recommend=#{store_recommend}  
   and s.store_name like #{store_name}
   and
    s.id in

      #{item}


  and
    a.id in

      #{item}


   and a.areaName like ${areaName}
   and p.store_evaluate1 >=#{store_evaluate1}
   and s.grade_id =#{grade_id}

select s.* from shopping_store s
left  join shopping_area a    on s.area_id =a.id
left join  shopping_store_point p on p.store_id= s.id

  1=1
   and  s.store_recommend=#{store_recommend}  
   and s.store_name like #{store_name}
  and
    s.id in

      #{item}


  and
    a.id in

      #{item}


   and a.areaName like #{areaName}
   and p.store_evaluate1 >=#{store_evaluate1}
   and s.grade_id =#{grade_id}
   order by concat("s.",#{orderBy})  ${sort}
   limit #{start},#{pageSize}
